use proc_macro::TokenStream;

/// A simple marker attribute for integration test functions.
///
/// This attribute does nothing at runtime - it simply marks functions
/// for discovery by the build script. The actual test execution logic
/// is generated by build.rs.
///
/// Usage: `#[test_case]`
#[proc_macro_attribute]
pub fn test_case(_args: TokenStream, input: TokenStream) -> TokenStream {
    // Simply return the input unchanged - this is just a marker
    input
}
